import React, { Component } from 'react';
import Title from "./Title";

export default class Order extends Component {
	constructor(props) {
		        super(props);
		
		        this.onChangeUsername = this.onChangeUsername.bind(this);
		        this.onChangeDescription = this.onChangeDescription.bind(this);
		        this.onChangeDuration = this.onChangeDuration.bind(this);
		        this.onChangeDate = this.onChangeDate.bind(this);
		        this.onSubmit = this.onSubmit.bind(this);
		
		        this.state = {
		            username: '',
		            description: '',
		            duration: 0,
		            date: new Date(),
		            users: []
		        }
		    }
    render() {
        return (
                            <div className="container">
                                <Title name="Order" title="Details" />
                                <div className="row justify-content-center">
								<div className=' form col-lg-5 col-md-8 col-sm-10 col-10 '>
									<div name='register'>
										<div className='form-group'>
											<label htmlFor='exampleInputEmail1'>Name:</label>
											<input
												type='text'
												name='name'
												className='form-control'
												id='name'
												aria-describedby='nameHelp'
												placeholder='Enter name'
												value={this.state.username}
                              				    onChange={this.onChangeUsername}
												required ></input>
										</div>
										<div className='form-group'>
											<label htmlFor='exampleInputEmail1'>Address:</label>
											<input
												type='email'
												name='email'
												className='form-control'
												id='email'
												aria-describedby='emailHelp'
												placeholder='Enter Address'
												value={this.state.address}
                              				    onChange={this.onChangeAddress}
												required ></input>
										</div>

										<div className='form-group'>
											<label htmlFor='exampleInputEmail1'>Phone:</label>
											<input
												type='text'
												name='phone'
												className='form-control'
												id='phone'
												aria-describedby='emailHelp'
												placeholder='Enter Phone Number'
												value={this.state.phone}
                              				    onChange={this.onChangePhone}
												required ></input>
										</div>
										<div className='form-group'>
											<label htmlFor='exampleInputEmail1'>Landmark:</label>
											<input
												type='text'
												name='password'
												id='password'
												className='form-control'
												aria-describedby='emailHelp'
												placeholder='Enter Landmark'
												value={this.state.landmark}
                              				    onChange={this.onChangeLandmark}
												required ></input>
										</div>
										{/* <div className='form-group'>
											<label htmlFor='exampleInputEmail1'>If Refferal Code:</label>
											<input
												type='text'
												className='form-control'
												placeholder='Enter Code'
												value={this.state.refferal}
                              				    onChange={this.onChangeRefferal} ></input>
										</div> */}
										</div>
									</div>
								</div>
                             </div>
        )
    }
}


