import React, { Component } from "react";
import Title from "../Title";
import CartColumns from "./CartColumns";
import CartList from "./CartList";
import CartTotals from "./CartTotals";
import { ProductConsumer } from "../../Context";
import EmptyCart from "./EmptyCart";
export default class Store extends Component {
  render() {
    return (
      <section>
        <ProductConsumer>
          {value => {
            const { cart } = value;
            if (cart.length > 0) {
              return (
                <React.Fragment>
                  {/* <Title name="your" title="cart" /> */}
                  {/* <h1 className="text-center text-white font-weight-bold">Your Cart</h1> */}
                  {/* <div className="row my-5 text-white"> */}
           <div className="col-10 my-2 text-center text-title">
                  <h1 className="text-uppercase font-weight-bold text-light mt-5">
                  Your <strong className="text-warning">
                      Cart
                      </strong>
                </h1> 
                </div>
                  <CartColumns />
                  <CartList value={value} />
                  <CartTotals value={value} history={this.props.history} />
                </React.Fragment>
              );
            } else {
              return <EmptyCart />;
            }
          }}
        </ProductConsumer>
      </section>
    );
  }
}
